import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import Navigation from '../components/Navigation';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('messages');
  const [pendingMessages, setPendingMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch pending messages and users
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch pending messages
        const messagesResponse = await axios.get('/api/messages/pending');
        if (messagesResponse.data.success) {
          setPendingMessages(messagesResponse.data.messages);
        }

        // Fetch users
        const usersResponse = await axios.get('/api/users');
        if (usersResponse.data.success) {
          setUsers(usersResponse.data.users);
        }
      } catch (error) {
        console.error('Error fetching admin data:', error);
        setError('Error loading data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Handle approving a message
  const handleApproveMessage = async (messageId) => {
    try {
      const response = await axios.put(`/api/messages/${messageId}/approve`);

      if (response.data.success) {
        // Remove from pending messages
        setPendingMessages((prev) => prev.filter((msg) => msg._id !== messageId));
      }
    } catch (error) {
      console.error('Error approving message:', error);
    }
  };

  // Handle deleting a message
  const handleDeleteMessage = async (messageId) => {
    try {
      const response = await axios.delete(`/api/messages/${messageId}`);

      if (response.data.success) {
        // Remove from pending messages
        setPendingMessages((prev) => prev.filter((msg) => msg._id !== messageId));
      }
    } catch (error) {
      console.error('Error deleting message:', error);
    }
  };

  // Handle promoting/demoting user admin status
  const handleToggleAdmin = async (userId, isAdmin) => {
    try {
      const response = await axios.put(`/api/users/${userId}`, {
        isAdmin: !isAdmin,
      });

      if (response.data.success) {
        // Update users list with new admin status
        setUsers((prev) =>
          prev.map((u) =>
            u._id === userId ? { ...u, isAdmin: !isAdmin } : u
          )
        );
      }
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <>
      <Navigation />
      <AdminContainer>
        <AdminHeader>
          <h1>Admin Panel</h1>
          <p>Manage messages and users</p>
        </AdminHeader>

        <TabsContainer>
          <Tab
            active={activeTab === 'messages'}
            onClick={() => setActiveTab('messages')}
          >
            Pending Messages
          </Tab>
          <Tab
            active={activeTab === 'users'}
            onClick={() => setActiveTab('users')}
          >
            User Management
          </Tab>
        </TabsContainer>

        {loading ? (
          <LoadingMessage>Loading...</LoadingMessage>
        ) : error ? (
          <ErrorMessage>{error}</ErrorMessage>
        ) : (
          <>
            {activeTab === 'messages' && (
              <ContentSection>
                <SectionTitle>Messages Awaiting Approval</SectionTitle>

                {pendingMessages.length === 0 ? (
                  <EmptyMessage>No messages awaiting approval.</EmptyMessage>
                ) : (
                  <MessageList>
                    {pendingMessages.map((message) => (
                      <MessageItem key={message._id}>
                        <MessageInfo>
                          <MessageSender>{message.sender?.username || 'User'}</MessageSender>
                          <MessageDate>
                            {new Date(message.createdAt).toLocaleString()}
                          </MessageDate>
                        </MessageInfo>
                        <MessageContent>{message.content}</MessageContent>
                        <MessageActions>
                          <ApproveButton onClick={() => handleApproveMessage(message._id)}>
                            Approve
                          </ApproveButton>
                          <RejectButton onClick={() => handleDeleteMessage(message._id)}>
                            Reject
                          </RejectButton>
                        </MessageActions>
                      </MessageItem>
                    ))}
                  </MessageList>
                )}
              </ContentSection>
            )}

            {activeTab === 'users' && (
              <ContentSection>
                <SectionTitle>User Management</SectionTitle>

                {users.length === 0 ? (
                  <EmptyMessage>No users found.</EmptyMessage>
                ) : (
                  <Table>
                    <thead>
                      <tr>
                        <th>Username</th>
                        <th>Status</th>
                        <th>Last Seen</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {users.map((u) => (
                        <tr key={u._id}>
                          <td>{u.username}</td>
                          <td>
                            {u.isAdmin ? (
                              <AdminBadge>Admin</AdminBadge>
                            ) : (
                              <UserBadge>Regular User</UserBadge>
                            )}
                          </td>
                          <td>{new Date(u.lastSeen).toLocaleString()}</td>
                          <td>
                            {u._id !== user._id && (
                              <ActionButton
                                isAdmin={u.isAdmin}
                                onClick={() => handleToggleAdmin(u._id, u.isAdmin)}
                              >
                                {u.isAdmin ? 'Remove Admin' : 'Make Admin'}
                              </ActionButton>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                )}
              </ContentSection>
            )}
          </>
        )}
      </AdminContainer>
    </>
  );
};

const AdminContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const AdminHeader = styled.div`
  text-align: center;
  margin-bottom: 2rem;

  h1 {
    color: #2c3e50;
    margin-bottom: 0.5rem;
  }

  p {
    color: #7f8c8d;
  }
`;

const TabsContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  border-bottom: 1px solid #e4e4e4;
`;

const Tab = styled.button`
  padding: 0.8rem 1.5rem;
  background-color: transparent;
  border: none;
  cursor: pointer;
  font-weight: 500;
