import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
import { io } from 'socket.io-client';
import { useAuth } from '../context/AuthContext';
import Navigation from '../components/Navigation';

const PrivateChat = () => {
  const { userId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [recipientUser, setRecipientUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const socketRef = useRef();
  const messagesEndRef = useRef(null);

  // Scroll to the bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Initialize socket connection and fetch messages
  useEffect(() => {
    if (!user || !userId) return;

    const serverUrl = process.env.REACT_APP_API_URL || '';
    socketRef.current = io(serverUrl);

    // Connect user
    socketRef.current.emit('user_connected', user.id);

    // Fetch user info and messages
    const fetchData = async () => {
      try {
        // Fetch recipient user info
        const userResponse = await axios.get(`/api/users/${userId}`);

        if (userResponse.data.success) {
          setRecipientUser(userResponse.data.user);

          // Fetch messages between current user and recipient
          const messagesResponse = await axios.get(`/api/messages/private/${userId}`);

          if (messagesResponse.data.success) {
            setMessages(messagesResponse.data.messages);
          }
        } else {
          setError('User not found');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error loading data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    // Cleanup socket connection
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [userId, user]);

  // Set up socket event listeners
  useEffect(() => {
    if (!socketRef.current || !user || !userId) return;

    // Listen for private messages
    socketRef.current.on('receive_private_message', (message) => {
      console.log('Received private message:', message);
      // Only add message if it's from the current chat
      if (
        (message.sender.id === parseInt(userId) && message.recipient.id === user.id) ||
        (message.sender.id === user.id && message.recipient.id === parseInt(userId))
      ) {
        setMessages((prevMessages) => [...prevMessages, message]);
      }
    });

    // Confirmation that message was sent
    socketRef.current.on('private_message_sent', (messageId) => {
      console.log('Private message sent:', messageId);
    });

    // Error handling
    socketRef.current.on('message_error', (error) => {
      console.error('Message error:', error);
      setError(error.error || 'Error sending message');
      setTimeout(() => setError(''), 3000);
    });

    return () => {
      socketRef.current.off('receive_private_message');
      socketRef.current.off('private_message_sent');
      socketRef.current.off('message_error');
    };
  }, [userId, user]);

  // Scroll to bottom when messages update
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Send a new message
  const sendMessage = (e) => {
    e.preventDefault();

    if (!newMessage.trim() || !user || !recipientUser) return;

    // Send message through socket
    socketRef.current.emit('send_private_message', {
      sender: user.id,
      recipient: parseInt(userId),
      content: newMessage.trim(),
    });

    // Optimistically add message to UI
    const optimisticMessage = {
      id: Date.now().toString(), // Temporary ID
      sender: {
        id: user.id,
        username: user.username,
      },
      recipient: {
        id: parseInt(userId),
        username: recipientUser?.username || 'User',
      },
      content: newMessage.trim(),
      createdAt: new Date().toISOString(),
    };

    setMessages((prev) => [...prev, optimisticMessage]);

    // Clear input
    setNewMessage('');
  };

  if (!user) {
    return <div>Please log in to view this page.</div>;
  }

  return (
    <>
      <Navigation />
      <ChatContainer>
        <ChatHeader>
          <BackLink to="/">Â« Back to Dashboard</BackLink>
          {loading ? (
            <div>Loading...</div>
          ) : error ? (
            <ErrorMessage>{error}</ErrorMessage>
          ) : (
            <>
              <h2>Chat with {recipientUser?.username}</h2>
            </>
          )}
        </ChatHeader>

        <MessagesContainer>
          {loading ? (
            <LoadingMessage>Loading messages...</LoadingMessage>
          ) : error ? (
            <ErrorMessage>{error}</ErrorMessage>
          ) : messages.length === 0 ? (
            <EmptyMessage>No messages yet. Start the conversation!</EmptyMessage>
          ) : (
            messages.map((message) => (
              <MessageBubble
                key={message.id || message._id}
                isOwnMessage={message.sender.id === user.id || message.sender._id === user.id}
              >
                <MessageContent>{message.content}</MessageContent>
                <MessageTime>
                  {new Date(message.createdAt).toLocaleTimeString()}
                </MessageTime>
              </MessageBubble>
            ))
          )}
          <div ref={messagesEndRef} />
        </MessagesContainer>

        <MessageForm onSubmit={sendMessage}>
          <MessageInput
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type your message..."
            disabled={loading || !!error || !recipientUser}
            autoFocus
          />
          <SendButton type="submit" disabled={loading || !!error || !recipientUser || !newMessage.trim()}>
            Send
          </SendButton>
        </MessageForm>
      </ChatContainer>
    </>
  );
};

const ChatContainer = styled.div`
  max-width: 900px;
  margin: 0 auto;
  height: calc(100vh - 60px);
  display: flex;
  flex-direction: column;
`;

const ChatHeader = styled.div`
  padding: 1.5rem;
  background-color: var(--bg-secondary);
  border-bottom: 1px solid var(--border-color);

  h2 {
    margin: 0.5rem 0;
    color: var(--text-primary);
    text-align: center;
  }
`;

const BackLink = styled(Link)`
  display: block;
  margin-bottom: 1rem;
  color: var(--accent-color);

  &:hover {
    text-decoration: underline;
  }
`;

const MessagesContainer = styled.div`
  flex: 1;
  padding: 1.5rem;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  background-color: var(--bg-primary);
`;

const LoadingMessage = styled.div`
  text-align: center;
  color: var(--text-secondary);
  margin: 2rem 0;
`;

const EmptyMessage = styled.div`
  text-align: center;
  color: var(--text-secondary);
  margin: 2rem 0;
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: var(--danger-color);
  font-weight: 500;
  margin: 1rem 0;
`;

const MessageBubble = styled.div`
  max-width: 70%;
  padding: 1rem;
  border-radius: 8px;
  background-color: ${(props) => (props.isOwnMessage ? 'var(--accent-color)' : 'var(--bg-secondary)')};
  color: ${(props) => (props.isOwnMessage ? 'var(--text-primary)' : 'var(--text-primary)')};
  align-self: ${(props) => (props.isOwnMessage ? 'flex-end' : 'flex-start')};
  box-shadow: 0 1px 2px var(--shadow-color);
`;

const MessageContent = styled.p`
  margin-bottom: 0.5rem;
  word-break: break-word;
`;

const MessageTime = styled.span`
  display: block;
  font-size: 0.7rem;
  opacity: 0.7;
  text-align: right;
  margin-top: 0.3rem;
`;

const MessageForm = styled.form`
  display: flex;
  padding: 1rem;
  background-color: var(--bg-secondary);
  border-top: 1px solid var(--border-color);
`;

const MessageInput = styled.input`
  flex: 1;
  padding: 0.75rem;
  border: 1px solid var(--border-color);
  border-radius: 4px 0 0 4px;
  font-size: 1rem;
  background-color: var(--bg-tertiary);
  color: var(--text-primary);

  &:focus {
    outline: none;
    border-color: var(--accent-color);
  }
`;

const SendButton = styled.button`
  background-color: var(--accent-color);
  color: var(--text-primary);
  border: none;
  border-radius: 0 4px 4px 0;
  padding: 0 1.5rem;
  font-size: 1rem;
  cursor: pointer;

  &:hover {
    background-color: var(--accent-hover);
  }

  &:disabled {
    background-color: #555;
    opacity: 0.7;
    cursor: not-allowed;
  }
`;

export default PrivateChat;
